        "url": "https://jobicy.com/jobs/102987-senior-product-designer-customer-growth",
        "jobSlug": "102987-senior-product-designer-customer-growth",
        "jobTitle": "Sr. Product Designer - Customer Growth",
        "companyName": "Thumbtack",
        "companyLogo": "https://jobicy.com/data/server-nyc0409/galaxy/mercury/2021/05/Jobicy-210503180426-842530.jpg",
        "jobIndustry": [
          "Web &amp; App Design"
        ],
